        query = """
            INSERT INTO components(component_type, name, content, "order")
            VALUES($1, $2, $3, $4)
            RETURNING id;
        """
        result = connection.execute(query, (self.component_type, self.name, self.content, self.order))
        self.id = result[0]['id']

